package com.hearhere.presentation.features.main.like

import androidx.lifecycle.MutableLiveData
import com.hearhere.presentation.base.BaseViewModel
import com.hearhere.presentation.features.main.MainViewModel
import com.hearhere.presentation.features.main.adapter.MarkerLikeItemBinder
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.asStateFlow
import kotlinx.coroutines.flow.update
import javax.inject.Inject

@HiltViewModel
class MarkerLikeViewModel @Inject constructor() : BaseViewModel() {
    private val _events = MutableStateFlow<List<MarkerLikeEvent>>(emptyList())
    val events get() = _events.asStateFlow()

    private val _uiState = MutableLiveData<List<MarkerLikeItemState>>(emptyList())
    val uiState get() = _uiState

    private val _binder = MutableLiveData<List<MarkerLikeItemBinder>>(emptyList())
    val binder get() = _binder

    init {
        //call API
        getMarkerList()
    }

    private fun getMarkerList() {
        val res = listOf<MarkerLikeItemState>(
            MarkerLikeItemState(
                1,
                "더미1",
                "artist1",
                "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRrIKNYOxhronFfiMMcOhnzwZRQJVXaX0mVuA&usqp=CAU",
                1.9
            ),
            MarkerLikeItemState(
                1,
                "더미1",
                "artist1",
                "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRrIKNYOxhronFfiMMcOhnzwZRQJVXaX0mVuA&usqp=CAU",
                1.9
            ),
            MarkerLikeItemState(
                1,
                "더미1",
                "artist1",
                "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRrIKNYOxhronFfiMMcOhnzwZRQJVXaX0mVuA&usqp=CAU",
                1.9
            ),
            MarkerLikeItemState(
                1,
                "더미1",
                "artist1",
                "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRrIKNYOxhronFfiMMcOhnzwZRQJVXaX0mVuA&usqp=CAU",
                1.9
            ),

            MarkerLikeItemState(
                1, "더미2", "artist2", "", 1.9
            ),
            MarkerLikeItemState(
                1, "더미2", "artist2", "", 1.9
            ),
            MarkerLikeItemState(
                1, "더미2", "artist2", "", 1.9
            ),
            MarkerLikeItemState(
                1, "더미2", "artist2", "", 1.9
            ),

            MarkerLikeItemState(
                1,
                "더미2",
                "artist2",
                "data:image/jpeg;base64,",
                1.9
            ),
            MarkerLikeItemState(
                1,
                "더미2",
                "artist2",
                "data:image/jpeg;base64,",
                1.9
            ),
        )
        _uiState.postValue(res)

        val binders = arrayListOf<MarkerLikeItemBinder>()
        res.forEach {
            val binder = MarkerLikeItemBinder(
                ::onClickDetail,
                ::onClickItemMenu
            )
            binder.setMarker(it)
            binders.add(binder)
        }.also {
            binder.postValue(binders)
        }
    }

    private fun onClickDetail(postId: Int) {
        //delete api 위치
        addEvent(MarkerLikeEvent.OnClickDetail(postId))
    }


    private fun onClickItemMenu(postId: Int, title: String) {
        addEvent(MarkerLikeEvent.ShowDialog(postId, title))
    }

    fun onClickItemDelete(postId: Int) {
        //delete api 위치
        addEvent(MarkerLikeEvent.DismissDialog)
    }

    fun onClickItemCopy(title: String) {
        //copy logic
        addEvent(MarkerLikeEvent.DismissDialog)
    }


    private fun addEvent(event: MarkerLikeEvent) {
        _events.update { it + event }
    }

    fun consumeViewEvent(event: MarkerLikeEvent) {
        _events.update { it - event }
    }


    data class MarkerLikeItemState(
        val postId: Int,
        val title: String,
        val artist: String,
        val coverPath: String? = "",
        val distance: Double,
    )

    sealed class MarkerLikeEvent() {
        data class OnClickDetail(val postId: Int) : MarkerLikeEvent()
        data class ShowDialog(val postId: Int, val title: String) : MarkerLikeEvent()
        object DismissDialog : MarkerLikeEvent()
    }
}